select  s._id   as subscription_id,  s._user as customer_id,  -- start_date: doðrudan CAST, olmazsa pattern parse, createdAt fallback  coalesce(    safe_cast(s.startDate   as timestamp),    safe.parse_timestamp('%Y-%m-%d %H:%M:%E*S', cast(s.startDate  as string)),    safe_cast(s.createdAt   as timestamp),    safe.parse_timestamp('%Y-%m-%d %H:%M:%E*S', cast(s.createdAt as string))  ) as start_date,  coalesce(    safe_cast(s.nextOrderDate as timestamp),    safe.parse_timestamp('%Y-%m-%d %H:%M:%E*S', cast(s.nextOrderDate as string))  ) as next_order_datefrom {{ source('raw','subscriptions') }} s